import React, {useState} from 'react';
import styles from './NationCard.module.scss'

export const NationCard = () => {
  const [activeIndex, setActiveIndex] = useState(0)

  const tabClickHandler = (index) => {
    setActiveIndex(index)
  }

  const tabContArr = [
    {
      index: 0,
      tabTitle: "여행지명0",
      tabCont: "여행지상세0"
    },
    {
      index: 1,
      tabTitle: "여행지명1",
      tabCont: "여행지상세1"
    },
    {
      index: 2,
      tabTitle: "여행지명2",
      tabCont: "여행지상세2"
    },
    {
      index: 3,
      tabTitle: "여행지명3",
      tabCont: "여행지상세3"
    }
  ]



  return (
    <>
      {tabContArr.map((section, index) => (
        <div key={index} className={styles.TabContentSection}>

          <div className={styles.CardContainer}>
            <div className={styles.TabContentImage}></div>
            <p className={styles.TabTitle}>{section.tabTitle}</p>
            <div className={styles.TabContentItem}>
              {Array.isArray(section.tabCont) ? (
                section.tabCont.map((item, idx)=> (
                  <span key={idx} className={styles.TabContentDetail}>
                    {item}
                  </span>
                ))
              ) : (
                <span className={styles.TabContentDetail}>
                  {section.tabCont}
                </span>
              )}
            </div>
          </div>

        </div>
      ))}
    </>
  )
}
