import React, { useEffect, useState } from "react";
import styles from "./Scroll.module.scss";

export const Scroll = () => {
  const [scrollStep, setScrollStep] = useState(0);

  const checkViewport = () => {
    const airplane = document.querySelector(`.${styles.Airplane1}`);
    if (airplane) {
      const rect = airplane.getBoundingClientRect();
      setIsInViewport(rect.top < window.innerHeight && rect.bottom > 0);
    }
  };

  const handleScroll = () => {
    const thirdSection = document.querySelector(`.${styles.thirdSection}`);
    if (thirdSection) {
      const rect = thirdSection.getBoundingClientRect();
      const isAtCenter = rect.left < window.innerWidth / 2 && rect.right > window.innerWidth / 2;

      if (isAtCenter) {
        setScrollStep((prevStep) => (prevStep < 3 ? prevStep + 1 : prevStep));
      }
    }
  };

  useEffect(() => {
    const handleScrollEvents = () => {
      handleScroll();
    };

    window.addEventListener("scroll", handleScrollEvents);
    return () => {
      window.removeEventListener("scroll", handleScrollEvents);
    };
  }, []);

  useEffect(() => {
    checkViewport();
    window.addEventListener("scroll", checkViewport);
    return () => {
      window.removeEventListener("scroll", checkViewport);
    };
  }, []);

  const renderThirdContent = () => {
    switch (scrollStep) {
      case 1:
        return (
          <>
            <img className={styles.mockupImg} src="/mockup1.png" alt="mockup1" />
            <div className={styles.thirdContentTexts}>
              <div className={styles.topCircle}>
                <p className={styles.topCircleText}>여행지 추천</p>
              </div>
              <p className={styles.middleText}>빠르게 여행계획 세우자!</p>
              <p className={styles.bottomText}>지금 현재 가기 좋은 여행지 추천받고 빠르게 결정해요.</p>
            </div>
          </>
        );
      case 2:
        return (
          <>
            <img className={styles.mockupImg} src="/mockup2.png" alt="mockup2" />
            <div className={styles.thirdContentTexts}>
              <div className={styles.topCircle}>
                <p className={styles.topCircleText}>카드 추천</p>
              </div>
              <p className={styles.middleText}>몰랐던 혜택 누리자!</p>
              <p className={styles.bottomText}>여행지 환율 우대받을 수 있는 카드도 한번에 알아봐요.</p>
            </div>
          </>
        );
      case 3:
        return (
          <>
            <img className={styles.mockupImg} src="/mockup3.png" alt="mockup3" />
            <div className={styles.thirdContentTexts}>
              <div className={styles.topCircle}>
                <p className={styles.topCircleText}>알림 서비스</p>
              </div>
              <p className={styles.middleText}>현명한 여행계획 세우자!</p>
              <p className={styles.bottomText}>알림받고 내가 정한 환율대로 예산 계획 세워요.</p>
            </div>
          </>
        );
      default:
        return (
          <>
            <img className={styles.mockupImg} src="/mockup1.png" alt="mockup1" />
            <div className={styles.thirdContentTexts}>
              <div className={styles.topCircle}>
                <p className={styles.topCircleText}>여행지 추천</p>
              </div>
              <p className={styles.middleText}>빠르게 여행계획 세우자!</p>
              <p className={styles.bottomText}>지금 현재 가기 좋은 여행지 추천받고 빠르게 결정해요.</p>
            </div>
          </>
        );
    }
  };

  return (
    <div className={styles.container}>
      {/* Existing content remains unchanged */}

      <div className={styles.thirdSection}>
        <div className={styles.thirdContent1}>{renderThirdContent()}</div>
      </div>

      {/* Existing content remains unchanged */}
    </div>
  );
};
