import React, {useState} from "react";
import styles from './SearchBar.module.scss';

export const SearchBar = () => {
    const [activeIndex, setActiveIndex] = useState(0)
    const tabClickHandler = (index) => {
        setActiveIndex(index)
    }

    const tabContArr = [
        {
            index: 0,
            tabTitle: "전체",
            tabCont: ["중국", "홍콩", "일본", 
                "브루나이", "인도네시아", "말레이시아", "싱가포르", "태국", 
                "아랍에미리트", "바레인", "쿠웨이트", "사우디",
                "미국", "캐나다",
                "유로존", "스위스", "덴마크", "영국", "노르웨이", "스웨덴",
                "호주", "뉴질랜드"
                ]
        },
        {
            index: 1,
            tabTitle: "동북아",
            tabCont: ["중국", "홍콩", "일본"]
        },
        {
            index: 2,
            tabTitle: "동남아",
            tabCont: ["브루나이", "인도네시아", "말레이시아", "싱가포르", "태국"]
        },
        {
            index: 3,
            tabTitle: "중동",
            tabCont: ["아랍에미리트", "바레인", "쿠웨이트", "사우디"]
        },
        {
            index: 4,
            tabTitle: "북미",
            tabCont: ["미국", "캐나다"]
        },
        {
            index: 5,
            tabTitle: "유럽",
            tabCont: ["유로존", "스위스", "덴마크", "영국", "노르웨이", "스웨덴"]
        },
        {
            index: 6,
            tabTitle: "오세안주",
            tabCont: ["호주", "뉴질랜드"]
        },
    ]



    return (
        <form className="searchBar">
            <div className={styles.inputContainer}>
                <label className={styles.inputLabel} htmlFor="travelDestination">여행지</label>
                <input className={styles.inputField} type="text" placeholder="여행지 검색" id="travelDestination" />
            </div>


            <div className={styles.TabSetting}>
                <ul className={styles.TabList}>
                    {tabContArr.map((section, index) => (
                        <p
                        key={index}
                        className={`${styles.TabItem} ${index === activeIndex ? styles.isActive : ''}`}
                        onClick={() => tabClickHandler(index)}
                        >
                        {section.tabTitle}
                        </p>
                    ))}
                    <div className={styles.ClickSlideWrapper}>
                        <div className={styles.ClickSlide} style={{ left: `${activeIndex * 14.2857}%` }}></div>
                    </div>
                </ul>

                <div>
                    {Array.isArray(tabContArr[activeIndex].tabCont) ? (
                        tabContArr[activeIndex].tabCont.map((item, idx) => (
                            <span key={idx} className={styles.TabContentItem}>
                                {item}
                            </span>
                        ))
                    ) : (
                        <span className={styles.TabContentItem}>
                            {tabContArr[activeIndex].tabCont}
                        </span>
                    )}
                </div>
                <button className={styles.ContinentBtn}>대륙 선택</button>
            </div>


            <div>
                <label htmlFor="budget">예산</label>
                <input type="button" placeholder="예산 입력" id="budget" />
            </div>

            <div>
                <label htmlFor="budgetInner">예산 입력</label>
                <input type="number" placeholder="원화로 예산을 입력하세요." id="budgetInner" />
                <span>원</span>
                <p>입력하신 예산을 기준으로 가장 가성비 좋은 여행지를 추천해드려요!</p>
            </div>

            <button>
                <img src="" alt="searchButton" />
            </button>
        </form>
    )
}
